mapper.update:
update product_m
set pname = #{pname,jdbcType=VARCHAR},
  pprice = #{pprice,jdbcType=DOUBLE},
  premain = #{premain,jdbcType=INTEGER},
  mid = #{mid,jdbcType=INTEGER}
where pid = #{pid,jdbcType=INTEGER}

mapper.select:
select pid, pname, pprice, premain, mid
from product_m
where pid = #{pid,jdbcType=INTEGER}
